const mvn = require('../../util/mvn')
const { readProject } = require('../../util/projectReader')
const { H2 } = require('../../constants').OPTIONS

module.exports = class H2Template extends require('../templateBase') {

  async canRun() {
    const { isNodejs } = await readProject()
    if (isNodejs) throw `'cds add ${H2}' is not available for Node.js`
    return true
  }

  async run() {
    await mvn.add(H2)
  }
}
