const { join } = require('path')
const cds = require('../../../cds'), { exists, copy } = cds.utils
const { readProject } = require('../../util/projectReader')

module.exports = class SampleTemplate extends require('../templateBase') {

    async canRun() {
        const { isJava } = await readProject()
        if (isJava) throw `'cds add sample' is not available for Java yet`
        return true
    }

    async run() {
        const { db, srv, app } = (await this.getEnv()).folders
        const language = exists('pom.xml') ? 'java' : 'nodejs'
        await Promise.all([
          copy(join(__dirname, 'files', 'db')).to(db),
          copy(join(__dirname, 'files', 'app')).to(app),
          copy(join(__dirname, 'files', 'srv')).to(srv),
          copy(join(__dirname, 'files', language)).to(srv)
        ])
    }
}
